                    </select>
                    <select className="ddl-in">
                        <option onSelect={()=>setMemory("256MB")}>256MB</option>
                        <option onSelect={()=>setMemory("512MB")}>512MB</option>
                        <option onSelect={()=>setMemory("1GB")}>1GB</option>
