import React from 'react';
import logo from './logo.svg';
import './App.css';
import {ImageCardList} from './components/ChooseImage';
// import {IPage} from './components/PageDisplay';
import {IBuild} from  './components/PageDisplay';
import {OBuild} from  './components/PageDisplay';
import {Bigprops} from './components/PageDisplay';

// let build:IBuild = {id:"",cost: "",name: "",version: "",instance: "",memory: "",type: "",networks:"",securityGroup:""};
// const [buildstate,setBuildState] = React.useState(build);

// export const setBuildStater = (ibuild : IBuild) => {
//   setBuildState(ibuild);
// }

const App = () => {


  let build:IBuild = {id:"",cost: 0,name: "",description:"",version: "",instance: "",memory: "",type: "",networks:"",securityGroup:""};
  let ibuild:OBuild = {istate:build};

  const [page,setPage] = React.useState("1");
  const [buildstate,setBuildState] = React.useState(build);



  // PageDisplay dpage={page} istate={build} upstate={setBuildStater}


  const setPage1 = () => {
    setPage("1");
  }
  const setPage2 = () => {
    setPage("2");
  }
  const setPage3 = () => {
    setPage("3");
  }
  const setPage4 = () => {
    setPage("4");
  }
  const setPage5 = () => {
    setPage("5");
  }

  // setBuildState(build);

  const setBuildStater = () => {
    setBuildState(build);
  }

  function PageDisplay(bgprops:Bigprops)  {
    // export const PageDisplay:React.FC<Bigprops> = (bgprops) => {        
        // const nav = props;
        // bgprops.dpage = "3"; Pass by reference

        bgprops.istate.id = "5";
        // bgprops.upstate();

        if(bgprops.dpage=="1"){
        return(
            // <ImageCardList dpage={bgprops.dpage} istate={bgprops.istate} upstate={bgprops.upstate}/>
            ImageCardList(bgprops)
        );
        }else if(bgprops.dpage=="2"){
        return(
            <h1>HI</h1>
        );
        }else if(bgprops.dpage=="3"){
        return(
            <h1>HI</h1>
        );
        }else if(bgprops.dpage=="4"){
        return(
            <h1>HI</h1>
        );
        }else if(bgprops.dpage=="5"){
        return(
            //  <Summary dpage={bgprops.dpage} istate={bgprops.istate} upstate={bgprops.upstate}/>
             Summary(bgprops)
            // <p></p>
        );

        }

        return null;

    };

  let sender : Bigprops = { dpage:page,istate:build,upstate:setBuildStater};
  // buildstate.id ="7";

  return (
    <div className="App">
      <main>

      <header className="App-header">
        <h3>HVC</h3>        
      </header>

      <body>

        {/* Main content */}
        <div className="content">

          <div style={{display:"inline"}}>
          <h1 className="body-title">Choose Image</h1>
          <select placeholder="Region">
            <option>us-1</option>
          </select>
          </div>

          <p>{buildstate.id}</p>
          <p>{buildstate.name}</p>
          <p>{buildstate.cost}</p>

          <nav className="navbar">
            <button className="navbutton" onClick={setPage1}>1.Choose Image</button>
            <button className="navbutton" onClick={setPage2}>2.Choose Instance Type</button>
            <button className="navbutton" onClick={setPage3}>3.Choose Storage and Network</button>
            <button className="navbutton" onClick={setPage4}>4.Configure Security</button>
            <button className="navbutton" onClick={setPage5}>5.Review and Launch</button>
          </nav>  

          {/* <PageDisplay dpage={page} istate={build} upstate={setBuildStater}/> */}
          {/* <PageDisplay dpage={page} istate={buildstate}/> */}
          {PageDisplay(sender)}


        </div> {/*Main content*/}

        {/*Cost card*/}
        <div className="cost-card">
          <h2 className="cost-card-header">Cost Estimates</h2>
          {/*details*/ }
          <p>{buildstate.name}</p>
          <br></br>
          <hr className="cost-card-line"></hr>
          <h2 className="dollar-cost">${buildstate.cost}/mo</h2>
        </div>{/*Cost card*/} 


      </body>{/*body*/}

      </main>

      {/* <h1>HI</h1> */}

    </div> 
  );
}

export default App;
